sys_script_include.script:
var TestScriptInclude = Class.create();
TestScriptInclude.prototype = {
    initialize: function() {},

    test: function() {
        var rightNow = new GlideDateTime();

        var todayAtMidnight = rightNow.toString().slice(0, 10) + " 23:59:59";
        var endOfToday = new GlideDateTime(todayAtMidnight);

        var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
		dueSoonNITasks.addQuery("due_date", ">=", rightNow);
		// check if due date is in the next 24hours
		dueSoonNITasks.addQuery("due_date", "<", gs.hoursAgo(-24));
        dueSoonNITasks.addQuery("state", "!=", 3);
        dueSoonNITasks.query();

        // Write a log message for each overdue NeedIt Task Record
        while (dueSoonNITasks.next()) {
            gs.info("######FIN Due soon NeedIt Task record = " + dueSoonNITasks.number);
        }
    },

    type: 'TestScriptInclude'
};
